const getAnimalsImg = async () => {
  const res = await fetch('https://fed-team.modyo.cloud/api/content/spaces/animals/types/game/entries?per_page=9');
  const data = await res.json();
  let urls = [];
  let j = 0;
  for (let i = 0; i < 18; ++i) {
    if (i!=0 && i!=1 && i % 2 == 0) ++j;
    urls[i] = data.entries[j].fields.image.url;  
  }
  const images = [
    {
      src: urls[0],
      name: 'Bear'
    },
    {
      src: urls[0],
      name: 'Bear'
    },
    {
      src: urls[1],
      name: 'Bird'
    },
    {
      src: urls[1],
      name: 'Bird'
