import React, { useState, useEffect } from 'react';
import Card from './Components/Card';	
import { images } from './import';
import './App.css';

function App() {

	const [cards, setCards] = useState([]);

	useEffect(() => {
		async function getImages() {
  			const res = await fetch('https://fed-team.modyo.cloud/api/content/spaces/animals/types/game/entries?per_page=9');
			const data = await res.json();
			let urls = [];
			let j = 0;
			for (let i = 0; i < 18; ++i) {
				if (i!=0 && i!=1 && i % 2 == 0) ++j;
				urls[i] = data.entries[j].fields.image.url;  
			}
			const animals = [
				{
				  src: urls[0],
				  name: 'Bear'
				},
				{
				  src: urls[0],
				  name: 'Bear'
				},
				{
				  src: urls[1],
				  name: 'Bird'
				},
				{
				  src: urls[1],
				  name: 'Bird'
				},
				{
				  src: urls[2],
				  name: 'Cat'
				},
				{
				  src: urls[2],
				  name: 'Cat'
				},
				{
				  src: urls[3],
				  name: 'Deer'
				},
				{
				  src: urls[3],
				  name: 'Deer'
				},
				{
				  src: urls[4],
				  name: 'Dog'
				},
				{
				  src: urls[4],
				  name: 'Dog'
				},
				{
				  src: urls[5],
				  name: 'Dolphin'
				},
				{
				  src: urls[5],
				  name: 'Dolphin'
				},
				{
				  src: urls[6],
				  name: 'Fish'
				},
				{
				  src: urls[6],
				  name: 'Fish'
				},
				{
				  src: urls[7],
				  name: 'Fox'
				},
				{
				  src: urls[7],
				  name: 'Fox'
				},
				{
				  src: urls[8],
				  name: 'Frog'
				},
				{
				  src: urls[8],
				  name: 'Frog'
				}
			];
			console.log(animals);
			setCards(animals);
		}
		getImages();		
	}, [])

	return (
		<div className='app'>
			<div className='cards-container'>
				{
					cards.map((card, i) => (
						<Card key={i}/>
					))	
				}			
			</div>
		</div>		
	)
	
}

export default App;
