import React, { useState } from 'react';
import ReactCardFlip from 'react-card-flip';
import backFace from './images/question.png';

const Card = ({ name, number, frontFace, flipCard }) => {

	const [isFlipped, setIsFlipped] = useState(false);
	
	const flip = e => {
		setIsFlipped(!isFlipped);
	}
	return (
		<div className='card'>
			<ReactCardFlip isFlipped={ isFlipped }>
				<img src={ backFace } alt='back-face' className="card-image" onClick={ flip } />	
				<img src={ frontFace } alt='back-face' className="card-image" onClick={ flip } />	
			</ReactCardFlip>
		</div>
	)	

}

export default Card;
