import React, { useState, useEffect } from 'react';
import Card from './Components/Card';	
import './App.css';

function App() {

	const [cards, setCards] = useState([]);

	const [firstCard, setFirstCard] = useState({});
  	const [secondCard, setSecondCard] = useState({});

  	const [unflippedCards, setUnflippedCards] = useState([]); // Arreglo de los números de cartas que necesitan volver su posición inicial
  	const [disabledCards, setDisabledCards] = useState([]);  // Arreglo de los números de cartas que necesitan ser deshabilitadas porque ya hicieron match 

	const shuffleArray = (array) => {
	    for (let i = array.length - 1; i > 0; i--) {
	      let j = Math.floor(Math.random() * (i + 1));
	      let temp = array[i];
	      array[i] = array[j];
	      array[j] = temp;
	    }
  	}

	useEffect(() => {
		async function getImages() {
  			const res = await fetch('https://fed-team.modyo.cloud/api/content/spaces/animals/types/game/entries?per_page=9');
			const data = await res.json();
			let urls = [];
			let j = 0;
			for (let i = 0; i < 18; ++i) {
				if (i != 0 && i != 1 && i % 2 == 0) ++j;
				urls[i] = data.entries[j].fields.image.url;  
			}
			const images = [
				{
				  src: urls[0],
				  name: 'Bear'
				},
				{
				  src: urls[1],
				  name: 'Bear'
				},
				{
				  src: urls[2],
				  name: 'Bird'
				},
				{
				  src: urls[3],
				  name: 'Bird'
				},
				{
				  src: urls[4],
				  name: 'Cat'
				},
				{
				  src: urls[5],
				  name: 'Cat'
				},
				{
				  src: urls[6],
				  name: 'Deer'
				},
				{
				  src: urls[7],
				  name: 'Deer'
				},
				{
				  src: urls[8],
				  name: 'Dog'
				},
				{
				  src: urls[9],
				  name: 'Dog'
				},
				{
				  src: urls[10],
				  name: 'Dolphin'
				},
				{
				  src: urls[11],
				  name: 'Dolphin'
				},
				{
				  src: urls[12],
				  name: 'Fish'
				},
				{
				  src: urls[13],
				  name: 'Fish'
				},
				{
				  src: urls[14],
				  name: 'Fox'
				},
				{
				  src: urls[15],
				  name: 'Fox'
				},
				{
				  src: urls[16],
				  name: 'Frog'
				},
				{
				  src: urls[17],
				  name: 'Frog'
				}
			];
			shuffleArray(images);
			setCards(images);
		}
		getImages();		
	}, [])

	const flipCard = (name, number) => {
	    if (firstCard.name == name && firstCard.number == number) // Se intenta voltear la misma carta
	      	return 0;
	    if (!firstCard.name)  // Aún no se ha volteado la primera carta
	    	setFirstCard({ name, number });
	    else if (!secondCard.name)  // Se ha volteado la primera carta pero aún no se ha volteado la segunda carta
	      	setSecondCard({ name, number });
	    return 1; // Devolverá 1 para los demás casos solo puede ser que tanto la primera como la segunda fueron volteadas
	}

	const checkForMatch = () => {
		if (firstCard.name && secondCard.name) { // Verifica primero que no sean vacías estas variables ya que al principio están vacías
			const match = firstCard.name == secondCard.name;
			match ? disableCards() : unflipCards();
		}
	}

	const resetCards = () => {
    	setFirstCard({});
    	setSecondCard({});
  	}

  	const unflipCards = () => {
  		setUnflippedCards([firstCard.number, secondCard.number]);
	    resetCards();
	};

	const disableCards = () => {
		setDisabledCards([firstCard.number, secondCard.number]);
	    resetCards();
	};

	return (
		<div className='app'>
			<div className='cards-container'>
				{

					cards.map((card, i) => (
						<Card 
							key={i} 
							name={card.name} 
							number={i} 
							frontFace={card.src} 
						flipCard={flipCard} />
					))	
				}			
			</div>
		</div>		
	)
	
}

export default App;
